mapper.insert:

	select seq_freeboard.nextval from dual

INSERT INTO FREEBOARD(
	   FR_NO, FR_TITLE
	 , FR_PARENT_NO
	 , FR_CONTENT, FR_HIT
	 , FR_IP,  FR_DEL_YN, FR_CLASS
) VALUES (
	#{fr_no}, #{fr_title}
	,#{fr_parent_no}
	,#{fr_content}, #{fr_hit}
	,'null'
	,'N' ,'null'
)

mapper.update:
	UPDATE freeboard 
		SET
		   FR_TITLE     = #{fr_title}
		 , FR_CONTENT   = #{fr_content}
		 , FR_MOD_DATE  = SYSDATE
		 , FR_DEL_YN    = 'N'
		 , FR_CLASS     = 'NULL'
  	 WHERE FR_NO = #{fr_no}

UPDATE freeboard
SET fr_hit = fr_hit + 1
WHERE fr_no = #{fr_no}
